import { useNavigate } from 'react-router-dom'
import './styles.css'

export const Todo = ({ d, data, setData }) => {




    const navigate = useNavigate()


    const deleteTodo = async () => {
        const fetchedData = await fetch(`https://jsonplaceholder.typicode.com/todos/${d.id}`, {
            method: "DELETE"
        })
        const parsedData = await fetchedData.json()
        console.log(parsedData);

        const deletedTodo = data.filter((i) => i.id !== d.id)
        setData(deletedTodo)


    }


    const editTodo = async () => {
        const fetchedData = await fetch(`https://jsonplaceholder.typicode.com/todos/${d.id}`, {
            method: "PATCH"
        })
        const parsedData = await fetchedData.json()
        console.log(parsedData);

        const deletedTodo = data.filter((i) => i.id !== d.id)
        setData(deletedTodo)
    }

    return (
        <div className="todo" >
            <div>
                <p>{d.title}</p>
                <button onClick={() => navigate(`${d.id}`)} >single page</button>
            </div>
            <div>
                <button onClick={() => deleteTodo()}>X</button>
            </div>
            <div>
                <button onClick={() => deleteTodo()}>EDIT</button>
            </div>
        </div>

    )
}
