import { useState } from 'react'
import './styles.css'

export const Header = ({data, setData}) => {


    const fatchTodos = async() => {
        const bodyData =  {
            userId: 1,
            id: Math.random(),
            title: addTitle,
            completed: false
    }


        const fetchedData = await fetch('https://jsonplaceholder.typicode.com/todos', {
            method: "POST" ,
            body:  bodyData
        })
        setData([...data,
            bodyData])
    }


    const [addTitle, setAddTitle] = useState('')

    return(
        <div className="header">
            <input type='text' onChange={(e) => setAddTitle(e.target.value) } value={addTitle}/>
            <button onClick={() => {
                fatchTodos()
                setAddTitle('')
            }
            }>ADD POST</button>
        </div>
    )
}
