import { useState } from 'react'
import './styles.css'

export const Header = ({data, setData}) => {


    const fatchTodos = async() => {
        const fetchedData = await fetch('https://jsonplaceholder.typicode.com/todos', {
            method: "POST" ,
            body:  {
                userId: 1,
                id: Math.random(),
                title: addTitle,
                completed: false
        }
        })
        const parsedData = await fetchedData.json()
        setData(parsedData.slice(0, 15))
    }


    const [addTitle, setAddTitle] = useState('')


    // function addPost() {
    //     return addTitle.length ? setData([...data,
    //         {
    //             userId: 1,
    //             id: Math.random(),
    //             title: addTitle,
    //             completed: false
    //     }]): 'null'

    // }





    return(
        <div className="header">
            <input type='text' onChange={(e) => setAddTitle(e.target.value) } value={addTitle}/>
            <button onClick={() => {
                fatchTodos()
                setAddTitle('')
            }
            }>ADD POST</button>
        </div>
    )
}
