import { useState } from 'react'
import { Link } from 'react-router-dom'
import './styles.css'

export const Todo = ({d}) => {

    const [todoById, setTodoById] = useState([])

    const fatchTodoById = async() => {
        const fetchedData = await fetch(`https://jsonplaceholder.typicode.com/todos/${d.id}`/* , {
            method: "GET" 
        } */)
        const parsedData = await fetchedData.json()
        setTodoById(parsedData)

        console.log(parsedData);
    }


    return(
        <Link to={`todos/${d.id}`}>
        <div className="todo" onClick={() => fatchTodoById() }>
            <p>{d.title}</p>
        </div>
        </Link>
    )
}
