import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import './styles.css'

export const Todo = ({ d, data, setData }) => {




    const navigate = useNavigate()
    const [editTitle, setEditTitle] = useState(d.title)
    const [editButton, setEditButton] = useState(false)

    const bodyData = {
        userId: 1,
        id: d.id,
        title: editTitle,
        completed: false
    }


    const deleteTodo = async () => {
        await fetch(`https://jsonplaceholder.typicode.com/todos/${d.id}`, {
            method: "DELETE"
        })

        const deletedTodo = data.filter((i) => i.id !== d.id)
        setData(deletedTodo)
    }



    const editTodo = async () => {
        const fetchedData = await fetch(`https://jsonplaceholder.typicode.com/todos/${d.id}`, {
            method: "PATCH",
            body: bodyData
        })
        const parsedData = await fetchedData.json()
        console.log(parsedData);
        const filterdData = data.map((i) => i.id !== d.id)
        console.log(filterdData);
        setEditButton()
    }

    return (
        <div className="todo" >
            <div>
                <p>{editButton ? <input type='text'  onChange={(e) => setEditTitle(e.target.value)} value={editTitle} /> : d.title }</p>
                <button onClick={() => navigate(`${d.id}`)} >single page</button>
            </div>
            <div>
                <button onClick={() => deleteTodo()}>X</button>
            </div>
            <div>
                {
                    editButton ? (
                        <button onClick={()=> editTodo()}>DONE</button>
                    ): <button onClick={() => setEditButton(true)}>EDIT</button>
                }
            </div>
        </div>

    )
}
